choose.when:
    			SELECT B.* FROM (
				SELECT ROWNUM as r, A.* FROM (
				SELECT book_board.* FROM book_board where title_info like '%'||#{search}||'%' ORDER BY no DESC)A 
				)B WHERE B.r BETWEEN #{begin} AND #{end}

choose.otherwise:
    			SELECT B.* FROM (
				SELECT ROWNUM as r, A.* FROM (
				SELECT book_board.* FROM book_board ORDER BY no DESC)A 
				)B WHERE B.r BETWEEN #{begin} AND #{end}
